describe("Zooming behavior",function(){var o,e,t;beforeEach(function(){o=L.map(document.createElement("div"),{zoomAnimation:!1}).setView([0,0],16),e=L.tileLayer("{x},{y},{z}",{minZoom:0,maxZoom:19}).addTo(o),t=o.zoomsliderControl._ui}),describe("Buttons",function(){it("Should be able to zoom in",function(){o.setZoom(16),expect(o.getZoom()).to.eql(16),happen.click(t.zoomIn),expect(o.getZoom()).to.eql(17)}),it("Should be able to zoom out",function(){o.setZoom(16),expect(o.getZoom()).to.eql(16),happen.click(t.zoomOut),expect(o.getZoom()).to.eql(15)})}),describe("Knob",function(){var e;beforeEach(function(){e=o.zoomsliderControl._knob}),it("Should set max value to be map maxZoom - minZoom",function(){expect(e._maxValue).to.eql(o.getMaxZoom()-o.getMinZoom())}),it("Should match the map zoom level",function(){o.zoomIn(),expect(o.getZoom()).to.eql(17),expect(e.getValue()).to.eql(17),o.setZoom(2),expect(o.getZoom()).to.eql(2),expect(e.getValue()).to.eql(2)})})});