describe("Control",function(){var o,e;it("Should add itself to the map unless zoomSliderControl is false",function(){o=L.map(document.createElement("div"),{zoomAnimation:!1}).setView([0,0],16),expect(o.zoomsliderControl).to.be.an(L.Control.Zoomslider)}),it("Should not add itself to the map if zoomSliderControl is set to false",function(){o=L.map(document.createElement("div"),{zoomAnimation:!1,zoomsliderControl:!1}).setView([0,0],16),expect(o.zoomsliderControl).to.be(void 0)}),it("The slider should move to the correct position when a layer is added",function(){o=L.map(document.createElement("div"),{zoomAnimation:!1}).setView([0,0],16),e=L.tileLayer("{x},{y},{z}",{minZoom:0,maxZoom:19}).addTo(o),expect(o.zoomsliderControl._knob.getValue()).to.eql(16)})});